const Cart = ({cart,setcart}) => {
    let subTotal=cart.reduce((sum,x)=>{
        return sum+((x.price*80)-((x.price*80)*10)/100)
    },0)
    return ( 
        <div className="cart">
            {cart.map((data)=>(
                <div className="carty">
                    <div className="cartyimg">
                        <img src={data.thumbnail} alt="" />
                    </div>
                    <div className="cartytitle">
                        <h1>{data.title}</h1>
                    </div>
                </div>
            ))}
            <h1>SubTotal: {subTotal}</h1>
        </div>
     );
}
 
export default Cart;
