import { useEffect, useState } from "react";

const VideosLogic = () => {
    let[state,setUsers]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
            let response=await fetch('http://localhost:4000/videos')
            let data=await response.json()  
            setUsers(data)  
        }
        fetchData()
    },[])
    function removed(id){
        setUsers(state.filter((s)=>s.id!=id))
    }
    return ( 
        <div className="vid">
        {/* <center><h1>{title}</h1></center> */}
        <div className="videologic">
            {state.length==0? "There are no videos to be deleted":state.map((x)=>{
                return(
                <div className="video">
                    <div className="thumb">
                        <img src={x.thumbnail} alt="" />
                    </div>
                    <div className="details">
                        <div className="cicon">
                            <img src={x.channelIcon} alt="" />
                        </div>
                        <div className="title">
                            <span id='title'>{x.title}</span><br />
                            <span>{x.channelname}</span><br />
                            <span>{x.views}M views</span>
                            <a onClick={()=>removed(x.id)} style={{cursor:"pointer",marginLeft:"230px"}}>Delete</a>
                        </div>
                    </div>
                </div>    
            )})}
            <button onClick={()=>data([])}>Delete All</button>
        </div>
        </div>
     
     );
}
export default VideosLogic;
